    private void btnIniciarSesionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIniciarSesionActionPerformed
        String DNI = txtUser.getText();
        String password = new String(txtPassword.getPassword());
        if ("admin".equals(DNI) && "admin".equals(password)) {
            VentanaPrincipalAdmin ventanaAdmin = new VentanaPrincipalAdmin();
            ventanaAdmin.setVisible(true);
            this.dispose();
        } 
        else {
            MiembroMesa miembro = buscarMiembroEnMesas(DNI, password);
            if (miembro != null) {
                SesionActiva.setUsuarioActivo(miembro);
                VentanaPrincipalMiembroMesa ventanaMiembroMesa = new VentanaPrincipalMiembroMesa();
                ventanaMiembroMesa.setVisible(true);
                this.dispose();
            }
            else {
                javax.swing.JOptionPane.showMessageDialog(this, "Usuario no reconocido", "Error", javax.swing.JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_btnIniciarSesionActionPerformed

