    private MiembroMesa buscarMiembroEnMesas(String DNI, String password) {
        LinkedList<Eleccion> elecciones = GestionEleccion.getLista();
        for (Eleccion eleccion : elecciones) {
            for (MesaElectoral mesa : eleccion.getMesas()) {
                if (mesa.getPresidente() != null 
                    && mesa.getPresidente().getDNI().equals(DNI) 
                    && mesa.getPresidente().getPassword().equals(password)) {
                    return mesa.getPresidente();
                }
                if (mesa.getSecretario() != null 
                    && mesa.getSecretario().getDNI().equals(DNI) 
                    && mesa.getSecretario().getPassword().equals(password)) {
                    return mesa.getSecretario();
                }
                if (mesa.getVocal() != null 
                    && mesa.getVocal().getDNI().equals(DNI) 
                    && mesa.getVocal().getPassword().equals(password)) {
                    return mesa.getVocal();
                }
            }
        }
        return null;
    }

